var commands = [
    
    {
        cmd: /(hello|hi)/gi,
        cb: (api, id)=>{
            api.sendMessage({
                body: 'Hello @Sender!',
                mentions: [{
                     tag: '@Sender',
                     id: id
                }],
            }, id);  
            return true;
        }
    },
    {
        cmd: /(how\s*(are|r)\s*(you|u)).*/ig,
        cb: (api, id)=>{
            api.sendMessage("Fine thanks!", id);
            return true;
        }
    },
    {
        cmd: /.*/gi,
        cb: (api, id)=>{
            api.sendMessage("Sorry i can`t Understad!", id);
            return true;
        }
    }




];


module.exports = commands;